import React, { useState } from 'react';
import { Anchor, Drawer, Button, Modal } from 'antd';
import { BarsOutlined } from '@ant-design/icons';

const { Link } = Anchor;

function AppHeader() {

  const [showApplicationModal, setShowApplicationModal] = useState(false);

  showModal = () => {
    setShowApplicationModal(true)
  };

  handleCancel = e => {
    setShowApplicationModal(false)
  };

  const [visible, setVisible] = useState(false);

  const showDrawer = () => {
    setVisible(true);
  };

  const onClose = () => {
    setVisible(false);
  };

  return (
    <>
    <div className="container-fluid">
      <div className="header">
        <div className="logo">
          <a href="/">GSTEM</a>
        </div>
        <div className="mobileHidden">
          <Anchor>
            <Link href="#about" title="About" />
            <Link onClick = {showModal()} href="/apply" title="Apply" />
            <Link href="#feature" title="Recent Event" />
            <Link href="#faq" title="FAQ" />
            <Link target='_blank' href="https://uapb.tfaforms.net/31" title="Donate" />
            <Link href="#contact" title="Contact" />
          </Anchor>
        </div>

        <Modal
            open={showApplicationModal}
            onCancel={handleCancel()}
            footer={null}
            destroyOnClose = {true}
          >
            <iframe width="360" height="315" src="https://www.youtube.com/embed/Joz6Jlpu93U" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" allowfullscreen></iframe>
          </Modal>

        <div className="mobileVisible">
          <Button type="primary" onClick={showDrawer}>
          <BarsOutlined />
          </Button>
          <Drawer
            placement="right"
            closable={true}
            onClose={onClose}
            open = {visible}
          >
            <Anchor>
              <Link href="#about" title="About" />
              <Link to="google.com" target='_blank' href="/apply" title="Apply" />
              <Link href="#feature" title="Recent Event" />
              <Link href="#faq" title="FAQ" />
              <Link target='_blank' href="https://uapb.tfaforms.net/31" title="Donate" />
              <Link href="#contact" title="Contact" />
            </Anchor>
          </Drawer>
        </div>
      </div>
    </div>
    </>

  );
}

export default AppHeader;
